import { Morph } from "lively.morphic/morph.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { pt } from "lively.graphics/index.js";

export default class Container extends Morph {
  makeFields(aNode) {
    this.reset();
    var fields = (Object.getOwnPropertyNames(aNode))
    fields.forEach(function(ea,i){
      var k = ea,
      v = aNode[ea]
      this.makeTextField(k,v,i)
    }.bind(this))
  }
  async makeTextField(label,text,index) {
    var labelMorph = await loadObjectFromPartsbinFolder("label");
    labelMorph.textString = label;
    var fieldMorph = await loadObjectFromPartsbinFolder("text");
    fieldMorph.textString = text
    labelMorph.padding = fieldMorph.padding
    fieldMorph.fontSize = labelMorph.fontSize
    var x = 10,y=(index*fieldMorph.height + 10)
    console.log({x,y})
    labelMorph.openInWorld(pt(x,y),this)
    fieldMorph.openInWorld(pt(x+50,y),this)
  }
  reset() {
    var self = this
    this.withAllSubmorphsDo(function(ea){
      if (ea != self){
        ea.remove();
      }
    })
  }
}

